import { createRouter, createWebHistory } from "vue-router";
import store from './Store/index.js';
import Home from './components/FrontPage/home.vue';
import Contact from './components/FrontPage/contact.vue';
import Login from './components/User/signup.vue';

const routes = [
  {
    path: '/',
    component: Home,
    name: 'home',
    meta: { requiresAuth: true },
  },
  {
    path: '/login',
    component: Login,
    name: 'login',
    meta: { requiresAuth: false },
  },
  { 
    path: '/contact', 
    component: Contact, 
    name: 'contact',
    meta: { requiresAuth: true },
  }
];

const router = createRouter({
  history: createWebHistory(),
  routes,
});

router.beforeEach((to, from, next) => {
  const isAuthenticated = store.getters['auth/isAuth'];
  
  if (to.meta.requiresAuth && !isAuthenticated) {
    next({ name: 'login' }); // Redirect to login if route requires authentication and user is not authenticated
  } else if (!to.meta.requiresAuth && isAuthenticated) {
    next({ name: 'home' }); // Redirect to home if route does not require authentication and user is authenticated
  } else {
    next(); // Continue navigation
  }
});

export default router;
