import { getAuth, onAuthStateChanged} from 'firebase/auth';
import { createRouter, createWebHistory } from "vue-router";
import store from './Store/vuex'
import Home from './components/FrontPage/home.vue'
import Contact from './components/FrontPage/contact.vue'

const router = createRouter({
  history: createWebHistory(),
  routes: [
    {
      path: '/',
      component: Home,
      name: 'home',
    },
    { 
        path: '/contact', 
        component: Contact, 
        name: 'contact',
        meta: { requiresAuth: true },
      
    }
  ]
});

const auth = getAuth();

const validateCheck = (to,from,next) => {
    if( to.meta.auth && !store.getters['auth/isAuth']){
        next('/');
    } else if( to.meta.signin && store.getters['auth/isAuth']){
        next('/contact');
    } else{
        next();
    }
}   


router.beforeEach((to,from,next)=>{
    if(from === START_LOCATION){
        const unsubscribe = onAuthStateChanged(auth, user => {
            if(user){
                store.dispatch('auth/autosign',user).then(()=>{
                    validateCheck(to,from,next)
                })
            } else {
                validateCheck(to,from,next)
            }
        })
        unsubscribe();
    } else {
        validateCheck(to,from,next)
    }    
})

export default router;
