/* eslint-disable */
import { createApp } from 'vue'
import App from './App.vue'
import store from './Store/index.js';
import router from './routes'
import VueSweetalert2 from 'vue-sweetalert2'
import './assets/style.css'
import './firebase'

const app = createApp(App)

router.beforeEach((to, from, next) => {
    if (to.name === 'login' && store.getters['auth/isAuth']) {
      next({ name: 'home' }); // Redirect to home if already logged in and trying to access login page
    } else if (to.matched.some(record => record.meta.requiresAuth)) {
      if (!store.getters['auth/isAuth']) {
        next({ name: 'login' }); // Redirect to login if not authenticated
      } else {
        next(); // Continue navigation
      }
    } else {
    console.log('User authenticated. Proceeding to', to.path);
      next(); // Continue navigation for routes that don't require authentication
    }
  });

router.beforeEach((to, from, next) => {
    firebase.auth().onAuthStateChanged(function(user) {
        if (to.matched.some(record => record.meta.requiresAuth)) {
            if (!user) {
                next({ path: '/login' });
            } else {
                next();
            }

        } else {
            next();
        }

        if (to.matched.some(record => record.meta.hideForAuth)) {
            if (user) {
                next({ path: '/dashboard' });
            } else {
                next();
            }
        } else {
            next();
        }
    });
});


app.use(store)
app.use(router)
app.use(VueSweetalert2)
app.mount('#app')
