import Vue from 'vue'
import Vuex from 'vuex'

Vue.use(Vuex)

const store = new Vuex.Store({
    state:{
        email: '',
        password: '',
        isSignup: false,
        isSignIn: false,
        showSignUpForm: false,
        showSignInForm: false,
    },
    mutations:{
        setShowSignInForm(state, value){
            state.showSignInForm = value
        },
        setShowSignUpForm(state, value){
            state.showSignUpForm = value
        }
    },
    actions:{
        
    },
    getters:{
     
    }
})

export default store
