/* eslint-disable */ 
//import { getAuth, onAuthStateChanged} from 'firebase/auth';
import { createRouter, createWebHistory } from "vue-router";
//import store from './Store'
import Home from './components/FrontPage/home.vue'
import Contact from './components/FrontPage/contact.vue'
import Login from './components/User/signup.vue'
import store from './Store'

const router = createRouter({
  history: createWebHistory(),
  routes: [
    {
      path: '/login',
      component: Login,
      name: 'login'
    },
    {
      path: '/',
      component: Home,
      name: 'home',
      meta: { requiresAuth: true },
    },
    { 
      path: '/contact', 
      component: Contact, 
      name: 'contact',
      meta: { requiresAuth: true },
    }
  ]
});

store.dispatch('init').then(() => {
router.beforeEach((to, from, next) => {
    if (to.name === 'home' && !store.getters['auth/isAuth']) {
      next({ name: 'login' }); // Redirect to home if already authenticated
    } else if(to.name === 'home' && store.getters['auth/isAuth']) {
      next({name: 'home'});
    } else {
      next();
    }
  })
})

export default router;
