import Vue from 'vue'
import Vuex, { createStore } from 'vuex'

Vue.use(Vuex)

const store = createStore({
    state:{
        food:''
    },
    mutations:{
        setFood(state,value){
            state.food = value
        }
    },
    actions:{
        
    },
    getters:{
        food: state => state.food
    }
})

export default store
