/* eslint-disable */
import {
    createUserWithEmailAndPassword,
    signInWithEmailAndPassword
} from 'firebase/auth';
import { doc, setDoc, getDoc } from 'firebase/firestore';
import { db, auth } from '../../firebase';


const DEFAULT_USER = {
    uid:null,
    email:null,
    firstname:null,
    lastname:null,
    isAdmin:null
}


const authModule = {
    namespaced:true,
    state(){
        return {
            user:DEFAULT_USER,
        }   
    },
    getters:{
        isAuth(state){
            if(state.auth) { return true};
            return false
        },
        isAdmin(state){
            if(state.user.isAdmin) { return true};
            return true
        },
        getUserData(state){
            return state.user;
        }
    },
    mutations:{
        setUser(state,payload){
            state.user = {
                ...state.user,
                ...payload
            }
            state.auth = true;
        },
        clearUser(state){
            state.user = DEFAULT_USER;
            state.auth = false;
        }
    },
    actions:{
        async signOut({commit}){
            try{
                await auth.signOut();
                commit('clearUser');
            } catch(error){
                console.log(error)
            }
        },
        async autosign({commit,dispatch},payload){
            try {
                const userData = await dispatch('getUserProfile',payload.uid);
                commit('setUser', userData);
                return true;
            } catch(error){
                console.log(error)
            }
        },
        async getUserProfile({commit},payload){
            try{
                const docSnap = await getDoc(doc(db,'users',payload));

                if(docSnap.exists()){
                    return docSnap.data();
                } else {
                    return null
                }
            } catch(error){
                console.log(error)
            }
        },
        async signin({commit,dispatch},payload){
            try {
                const userCredential = await signInWithEmailAndPassword(
                    auth,
                    payload.email,
                    payload.password
                );
                
                const userData = await dispatch('getUserProfile',userCredential.user.uid);
                commit('setUser',userData);

                router.push('/user/dashboard');                
            } catch(error){
                console.log(error)     
            }
        },
        async signup({commit},payload){
            try{
        
                const userCredential = await createUserWithEmailAndPassword(
                    auth,
                    payload.email,
                    payload.password
                );

                const newUser = {
                    uid:userCredential.user.uid,
                    email:userCredential.user.email,
                    isAdmin:false /// warning -  cloud function
                }
                await setDoc(doc(db,'users',userCredential.user.uid),newUser);
                commit('setUser',newUser);
                router.push('/user/dashboard');    
            }catch(error){
                console.log(error)
            }
        }
    }
}

export default authModule;
