/* eslint-disable */ 
import { getAuth, onAuthStateChanged} from 'firebase/auth';
import { createRouter, createWebHistory, START_LOCATION } from "vue-router";
import store from './Store'
import Home from './components/FrontPage/home'
import Contact from './components/FrontPage/contact'
import Login from './components/User/signup'

const router = createRouter({
  history: createWebHistory(),
  routes: [
    {
      path: '/login',
      component: Login,
      name: 'login'
    },
    {
      path: '/',
      component: Home,
      name: 'home',
      meta: { requiresAuth: true },
    },
    { 
      path: '/contact', 
      component: Contact, 
      name: 'contact',
      meta: { requiresAuth: true },
    }
  ]
});

const auth = getAuth();

const validateCheck = (to,from,next) => {
    if( to.meta.home && !store.getters['auth/isAuth']){
        next('/login');
    } else if( to.meta.signin && store.getters['auth/isAuth']){
        next('/');
    } else{
        next();
    }
}   


router.beforeEach((to,from,next)=>{
    if(from === START_LOCATION){
        const unsubscribe = onAuthStateChanged(auth, user => {
            if(user){
                store.dispatch('auth/autosign',user).then(()=>{
                    validateCheck(to,from,next)
                })
            } else {
                validateCheck(to,from,next)
            }
        })
        unsubscribe();
    } else {
        validateCheck(to,from,next)
    }    
})

export default router;
