/* eslint-disable */ 
//import { getAuth, onAuthStateChanged} from 'firebase/auth';
import { createRouter, createWebHistory } from "vue-router";
import store from './Store/auth'
import Home from './components/FrontPage/home.vue'
import Contact from './components/FrontPage/contact.vue'
import Login from './components/User/signup.vue'

const router = createRouter({
  history: createWebHistory(),
  routes: [
    {
      path: '/',
      component: Home,
      name: 'home',
      meta: {auth: true},
    },
    {
      path: '/login',
      component: Login,
      name: 'login',
      meta: {auth: false}
    },
    { 
      path: '/contact', 
      component: Contact, 
      name: 'contact',
      meta: {auth: true}
    }
  ]
});

router.beforeEach((to, from, next) => {
    if (to.meta.auth && !store.getters['auth/isAuth']){
    next('/login')
  } else if(!to.meta.auth && store.getters['auth/isAuth']){
    next()
  }
});


export default router;
