/* eslint-disable */ 
//import { getAuth, onAuthStateChanged} from 'firebase/auth';
import { createRouter, createWebHistory } from "vue-router";
//import store from './Store'
import Home from './components/FrontPage/home.vue'
import Contact from './components/FrontPage/contact.vue'
import Login from './components/User/signup.vue'

const router = createRouter({
  history: createWebHistory(),
  routes: [
    {
      path: '/login',
      component: Login,
      name: 'login'
    },
    {
      path: '/',
      component: Home,
      name: 'home',
      meta: { requiresAuth: true },
    },
    { 
      path: '/contact', 
      component: Contact, 
      name: 'contact',
      meta: { requiresAuth: true },
      
    }
  ]
});

router.beforeEach((to, from, next) => {
  if (!to.name === 'login' && !store.getters['auth/isAuth']) {
    next('/login'); // Redirect to home if already logged in and trying to access login page
  } else if(to.name === 'home' && store.getters['auth/isAuth']){
      next('/');
  } else {
    next(); // Continue navigation for routes that don't require authentication
  }
});


export default router;
