import {
    signInWithEmailAndPassword
} from 'firebase/auth';
import { auth } from '../../firebase';

const AuthModule = {
    state:{ 
        email:'',
        password:'',
        userId:'',
        isLogIn: false,
        user: null,
        userId:''
    },
    mutations:{
        setLogIn(state){
            state.isLogIn = true;
        },
        setUser(state,user){
            state.user = user
        }
    },
    actions:{
        async signIn({ commit }, { email, password}){
            try {
                await signInWithEmailAndPassword(auth, email, password, userId);
                const user = auth.currentUser.uid
                commit('setUser', user)
                commit('setLogIn')
            } catch (error) {
                console.log(error)
            }
        },
    },
    getters:{
        email: (state )=> state.email,
        password: (state) => state.password,
        isLogIn: (state)=> state.isLogIn,
        user: (state) => state.user,
        userId: (state) => state.userId
    }
}

export default AuthModule;
